              <label for=\"stringInput\">String</label>
              <input type=\"text\" name=\"stringValue\" class=\"form-control\" aria-describedby=\"emailHelp\" placeholder=\"Enter a string\">
              <small id=\"emailHelp\" class=\"form-text text-muted\">Type sum shit</small>
            </div>
            <div class=\"form-group\">
              <label for=\"numberInput\">Number</label>
              <input type=\"number\" class=\"form-control\" name=\"numberInput\" placeholder=\"enter some number\">
